import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../Style.css";

const Navbar = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [isLoggedIn, setLoggedIn] = useState(false); // Simule un utilisateur connecté
  const [user, setUser] = useState({ firstName: "John", lastName: "Doe" }); // Test utilisateur

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  return (
    <nav className="navbar">
      <div className="brand">
        <Link to="/">CHRONOLITHE.</Link>
      </div>
      <ul>
        <li>
          <Link to="/shop">Shop</Link>
        </li>
        <li>
          <Link to="/about">À Propos</Link>
        </li>
        <li>
          <Link to="/contact">Contact</Link>
        </li>
      </ul>
      <div className="auth-icon" onClick={toggleDropdown}>
        {/* Icône de connexion */}
        <img
          src="https://cdn-icons-png.flaticon.com/512/1077/1077063.png"
          alt="User Icon"
          className="auth-icon-image"
        />
        {isDropdownOpen && (
          <div className="dropdown-menu">
            {!isLoggedIn ? (
              <>
                <Link to="/login">Se connecter</Link>
                <Link to="/signup">Créer un compte</Link>
              </>
            ) : (
              <p>{`${user.firstName} ${user.lastName}`}</p>
            )}
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
